package varables;

public class leapyear {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//leap year or not
		int year=2025;
		if(year%4==0) {
			System.out.println("is leap year ");
		}
		else {
			System.out.println("non leap year ");
		}

	}

}
